import React from "react";
import Pagination from "react-bootstrap/Pagination";
import { useState } from 'react';

const Paginations = ({ postsPerPage, totalPosts, paginate, currentPage ,itemsPerPage}) => {
  const pageNumbers = [];
const [selectedItemsPerPage, setSelectedItemsPerPage] = useState(itemsPerPage);
  for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
    pageNumbers.push(i);
  }

  console.log(selectedItemsPerPage)

    function handleItemsPerPageChange(e) {
    setSelectedItemsPerPage(Number(e.target.value));

  }


  return (
    <Pagination className="d-flex justify-content-center">
           <div className="mr-3">
        <label htmlFor="itemsPerPageSelect">Items per page:</label>
        <select id="itemsPerPageSelect" value={selectedItemsPerPage} onChange={handleItemsPerPageChange}>
          <option value="5">5</option>
          <option value="10">10</option>
          <option value="15">15</option>
          <option value="20">20</option>
        </select>
      </div>
      <Pagination.Prev
        disabled={currentPage === 1}
        onClick={() => paginate(currentPage - 1)}
      />
      {pageNumbers.map((number) => (
        <Pagination.Item
          key={number}
          active={number === currentPage}
          onClick={() => paginate(number)}
        >
          {number}
        </Pagination.Item>
      ))}
      <Pagination.Next
        disabled={currentPage === pageNumbers[pageNumbers.length - 1]}
        onClick={() => paginate(currentPage + 1)}
      />
    </Pagination>

  );
};

export default Paginations;
